package ucar.utils;

import java.util.ArrayList;
import java.util.Date;
import java.util.Iterator;
import java.util.List;
import java.util.Properties;

import javax.mail.Message;
import javax.mail.MessagingException;
import javax.mail.Session;
import javax.mail.Transport;
import javax.mail.internet.InternetAddress;
import javax.mail.internet.MimeMessage;

import org.apache.commons.logging.Log;
import org.apache.commons.logging.LogFactory;

/\*\*
\* Utility class to send email
\*/
public class Email \{

static final Log logger = LogFactory.getFactory\(\).getInstance\("java.lang.Object"\);

/\*\*
\* Static method to send an email.  This method can be used to send to multiple recipients and it can also
\* set the content type accordingly.
\*
\* @param from email address of the sender
\* @param to ArrayList of recipients
\* @param subject the subject of the email
\* @param content the content of the tmail
\* @param contentType the content type \(text/plain, text/html..etc\)
\* @throws MessagingException thrown if there is a problem while sending the email.
\*/
public static void send\(String from, List to, String subject, String content, String contentType\)
throws Exception \{

if \(from == null || to.size\(\) == 0\) \{
throw new Exception\("Missing TO or FROM address"\);
\}

try \{

Properties props = System.getProperties\(\);
//props.put\("mail.smtp.host", emailServer\);
Session emailsession = Session.getDefaultInstance\(props, null\);

Message email = new MimeMessage\(emailsession\);
email.setFrom\(new InternetAddress\(from\)\);
InternetAddress\[\] address = new InternetAddress\[to.size\(\)\];
for \(int i = 0; i &lt; to.size\(\); i++\) \{
address\[i\] = new InternetAddress\(\(String\) to.get\(i\)\);
\}
email.setRecipients\(Message.RecipientType.TO, address\);
email.setSubject\(subject\);
email.setSentDate\(new Date\(\)\);
email.setHeader\("X-Mailer", ""\);
email.setContent\(content, contentType\);
Transport.send\(email\);

\} catch\(MessagingException e\) \{

logger.warn\("Error sending email:"+e.getMessage\(\)\);
logger.warn\("\tFrom: "+from\);
for \(Iterator iter=to.iterator\(\); iter.hasNext\(\);\) \{
logger.warn\("\tTo: "+\(String\)iter.next\(\)\);
\}
logger.warn\("\tSubject: "+subject\);
logger.warn\("\tContent: "+content\);

\} // MessagingException

\}

public static void send\(String from, List to, String subject, StringBuffer content, String contentType\) throws Exception \{
send\(from, to,subject, content.toString\(\), contentType\);
\} // send\(\)

/\*\*
\* Static method to send an email.  This method should can be used when sending email to multiple recipients
\* and if and only if the desired content type is 'text/plain'
\*
\* @param from: email address of the sender
\* @param to: array containing recipient emails
\* @param subject: the subject of the email
\* @param content: the content of the email
\*/
public static void send\(String from, List to, String subject, String content\)
throws Exception \{
send\(from, to, subject, content, "text/plain"\);
\} // send\(\)

public static void send\(String from, List to, String subject, StringBuffer content\)
throws Exception \{
send\(from, to, subject, content.toString\(\), "text/plain"\);
\} // send\(\)

/\*\*
\* Static method to send an email.  This can be used when sending email to 1 person and
\* if and only if the desired content type is 'text/plain'
\*
\* @param from: email address of the sender
\* @param to: recipient email
\* @param subject: the subject of the email
\* @param content: the content of the email
\*/
public static void send\(String from, String to, String subject, String content\) throws Exception \{

ArrayList list = new ArrayList\(\);
list.add\(to\);
send\(from, list, subject, content\);

\} // send\(\)

public static void send\(String from, String to, String subject, StringBuffer content\) throws Exception \{
send\(from, to, subject, content.toString\(\)\);
\}

\} // class Email

